	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of Saison objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptSerie(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		SaisonPeer::addSelectColumns($criteria);
		$startcol2 = (SaisonPeer::NUM_COLUMNS - SaisonPeer::NUM_LAZY_LOAD_COLUMNS);

		PersonnePeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (PersonnePeer::NUM_COLUMNS - PersonnePeer::NUM_LAZY_LOAD_COLUMNS);

		VersionPeer::addSelectColumns($criteria);
		$startcol4 = $startcol3 + (VersionPeer::NUM_COLUMNS - VersionPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(SaisonPeer::REALISATEUR_ID, PersonnePeer::ID, $join_behavior);

		$criteria->addJoin(SaisonPeer::VERSION_GENERALE_ID, VersionPeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseSaisonPeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = SaisonPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = SaisonPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = SaisonPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				SaisonPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Personne rows

				$key2 = PersonnePeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = PersonnePeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = PersonnePeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					PersonnePeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Saison) to the collection in $obj2 (Personne)
				$obj2->addSaison($obj1);

			} // if joined row is not null

				// Add objects for joined Version rows

				$key3 = VersionPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = VersionPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = VersionPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					VersionPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Saison) to the collection in $obj3 (Version)
				$obj3->addSaison($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


