			}


			if (($retval = SeriePeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}


				if ($this->collSaisons !== null) {
					foreach ($this->collSaisons as $referrerFK) {
						if (!$referrerFK->validate($columns)) {
