			} else {

				$criteria->add(SerieproprietairePeer::SERIE_ID, $this->id);

				$this->collSerieproprietaires = SerieproprietairePeer::doSelectJoinsfGuardUser($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(SerieproprietairePeer::SERIE_ID, $this->id);

			if (!isset($this->lastSerieproprietaireCriteria) || !$this->lastSerieproprietaireCriteria->equals($criteria)) {
				$this->collSerieproprietaires = SerieproprietairePeer::doSelectJoinsfGuardUser($criteria, $con, $join_behavior);
			}
		}
		$this->lastSerieproprietaireCriteria = $criteria;

		return $this->collSerieproprietaires;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collSaisons) {
				foreach ((array) $this->collSaisons as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCategorieseries) {
				foreach ((array) $this->collCategorieseries as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSerieproprietaires) {
				foreach ((array) $this->collSerieproprietaires as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collSaisons = null;
		$this->collCategorieseries = null;
		$this->collSerieproprietaires = null;
			$this->aPersonne = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSerie:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSerie::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

